import Options from "./Options";
import { useQuiz } from "./context/QuizContext";

function Question() {
  const { question, answer } = useQuiz();
  return (
    <div>
      <h4>{question.question}</h4>
      <Options
        question={question}
        dispatch={dispatch}
        answer={answer}
      ></Options>
    </div>
  );
}

export default Question;
