import Options from "./Options";
import { useQuiz } from "./context/QuizContext";

function Question() {
  const { questions } = useQuiz();
  return (
    <div>
      <h4>{questions.question}</h4>
      <Options></Options>
    </div>
  );
}

export default Question;
