function Question({ question }) {
  return (
    <div>
      <h4>{question.question}</h4>

      <div className="options">
        {question.options.map((option) => {
          return <button className="btn btn-option">{option}</button>;
        })}
      </div>
    </div>
  );
}

export default Question;
